import React, { useEffect, useState } from 'react';
import { View, Text, FlatList, TouchableOpacity } from 'react-native';
import { collection, getDocs, doc, getDoc, query, orderBy } from 'firebase/firestore';
import { firestore, auth } from '../firebase/config';
import PostItem from './PostItem';
import { BlurView } from 'expo-blur';

const Feed = () => {
  const [posts, setPosts] = useState([]);
  const [userId, setUserId] = useState(null);
  const [visible, setVisible] = useState(null)

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(user => {
      if (user) {
        setUserId(user.uid);
      } else {
        setUserId(null);
        const timer = setTimeout(() => {
          setVisible(true);
        }, 5000); // Espera 5 segundos

        // Limpa o timer se o componente for desmontado
        return () => clearTimeout(timer);
      }
    });


    const fetchPosts = async () => {
      try {
        const postsQuery = query(collection(firestore, 'posts'), orderBy('data', 'desc'));
        const postSnapshot = await getDocs(postsQuery);

        const postList = await Promise.all(
          postSnapshot.docs.map(async (postDoc) => {
            const postData = { id: postDoc.id, ...postDoc.data() };

            if (postData.autor) {
              const userDocRef = doc(firestore, 'users', postData.autor);
              const userDoc = await getDoc(userDocRef);
              postData.autor = userDoc.exists() ? { id: userDoc.id, ...userDoc.data() } : null;
            } else {
              postData.autor = null;
            }
            return postData;
          })
        );

        setPosts(postList);

      } catch (error) {
        console.error("Erro ao buscar posts: ", error);
      }
    };

    fetchPosts();
    return () => unsubscribe();
  }, []);

  return (
    <View style={{ flex: 1 }}>
      {visible && ( // Renderiza a mensagem de login apenas se 'visible' for true
        <BlurView intensity={75} style={{
         position:'absolute',
          width:'100%',

          textAlign: 'center',
          justifyContent: 'center',
          overflow: 'hidden',
         height:'100%',
        zIndex: 2
        }}>
          <View style={{ backgroundColor:"#FFF", padding:20, borderWidth:1, borderRadius:24, justifyContent:'center', alignItens:'center', marginHorizontal:'10%' }}>
          <Text style={{textAlign:'center', borderBottomWidth:1, fontSize:24, fontWeight:'bold'}}>ùïê</Text>
            <Text style={{textAlign:'center'}}>Entre para continuar</Text>
            <TouchableOpacity>
              <Text style={{textAlign:'center', backgroundColor:'#00BFFF', padding:30}}>Entrar</Text>
            </TouchableOpacity>
          </View>

        </BlurView>
      )}
      <FlatList
        data={posts}
        renderItem={({ item }) => (
          <PostItem
            texto={item.texto}
            img={item.img}
            autor={item.autor}
            id={item.id}
            userId={userId}
            dell={false}
          />
        )}
        keyExtractor={(item) => item.id}
      />
    </View>
  );
};

export default Feed;
