import React, { useState } from 'react';
import { View, TextInput, Text, StyleSheet, TouchableOpacity } from 'react-native';
import { auth } from '../firebase/config';
import { createUserWithEmailAndPassword, fetchSignInMethodsForEmail, signInWithPopup, GoogleAuthProvider } from 'firebase/auth';
import { firestore } from '../firebase/config';
import { doc, setDoc, getDocs, collection, query, where } from 'firebase/firestore';
import { useNavigation } from '@react-navigation/native';

const RegisterScreen = () => {
  const navigation = useNavigation();
  const [isActive, setIsActive] = useState(false);
  const [nome, setNome] = useState('');
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleRegister = async () => {
    setErrorMessage('');

    if (password !== confirmPassword) {
      setErrorMessage('As senhas n칚o coincidem.');
      return;
    }

    try {
      const emailExists = await checkEmailExists(email);
      if (emailExists) {
        setErrorMessage('Este e-mail j치 est치 registrado.');
        return;
      }

      const q = query(collection(firestore, 'users'), where('username', '==', username));
      const querySnapshot = await getDocs(q);

      if (querySnapshot.empty) {
        const userCredential = await createUserWithEmailAndPassword(auth, email, password);
        const user = userCredential.user;

        await setDoc(doc(firestore, 'users', user.uid), {
          uid: user.uid,
          nome,
          username,
          email,
        });

        navigation.navigate('Perfil');
      } else {
        setErrorMessage('Este nome de usu치rio j치 est치 em uso.');
      }

    } catch (error) {
      setErrorMessage('Erro ao criar conta. Tente novamente.');
    }
  };

  const checkEmailExists = async (email) => {
    try {
      const signInMethods = await fetchSignInMethodsForEmail(auth, email);
      return signInMethods.length > 0;
    } catch (error) {
      setErrorMessage('Erro ao verificar o e-mail.');
      return false;
    }
  };

  const handleGoogleLogin = async () => {
    const provider = new GoogleAuthProvider();

    try {
      const result = await signInWithPopup(auth, provider);
      const user = result.user;

      // Registre o usu치rio no Firestore
      await setDoc(doc(firestore, 'users', user.uid), {
        uid: user.uid,
        nome: user.displayName,
        username: user.email.split('@')[0], // Usa a parte do e-mail como nome de usu치rio
        email: user.email,
      });

      navigation.navigate('Perfil');
    } catch (error) {
      setErrorMessage('Erro ao entrar com o Google. Tente novamente.');
    }
  };

  function handleLogin() {
    navigation.navigate('Login');
  }

  return (
    <View style={styles.container}>
      <Text style={{ fontSize: 55, marginBottom: 40 }}>洧뎷</Text>
      <Text style={{ fontSize: 20, marginBottom: 20 }}>Cadastre-se</Text>

      {/* Outros TextInput */}
      <TextInput
        style={styles.input}
        placeholder="Nome"
        value={nome}
        onChangeText={setNome}
        keyboardType="default"
        autoCapitalize="words"
        placeholderTextColor={isActive ? '#aaa' : '#ccc'}
        onFocus={() => setIsActive(true)}
        onBlur={() => { if (nome === '') setIsActive(false); }}
      />
      {/* ... outros campos de entrada ... */}

      {errorMessage ? <Text style={styles.error}>{errorMessage}</Text> : null}

      <TouchableOpacity style={styles.button} onPress={handleRegister}>
        <Text style={{ color: '#FFF', fontSize: 19 }}>Cadastrar</Text>
      </TouchableOpacity>

      <TouchableOpacity style={styles.button} onPress={handleGoogleLogin}>
        <Text style={{ color: '#FFF', fontSize: 19 }}>Entrar com Google</Text>
      </TouchableOpacity>

      <Text style={styles.registerPrompt}>J치 possui uma conta?</Text>
      <TouchableOpacity onPress={handleLogin}>
        <Text style={{ textDecorationLine: 'underline', color: 'blue' }}>Entrar</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f2f2f2',
    padding: 20,
  },
  input: {
    width: '100%',
    height: 50,
    borderColor: '#ccc',
    borderWidth: 1,
    borderRadius: 25,
    paddingLeft: 15,
    paddingVertical: 8,
    marginBottom: 20,
    fontSize: 16,
    backgroundColor: '#F5F8FA',
  },
  button: {
    width: '40%',
    height: 40,
    backgroundColor: '#1DA1F2',
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 25,
    marginTop: 20,
  },
  error: {
    color: 'red',
    fontSize: 14,
    marginBottom: 10,
    textAlign: 'center',
  },
  registerPrompt: {
    marginTop: 20,
    fontSize: 16,
  },
});

export default RegisterScreen;
